import React, { useEffect, useState } from 'react';
import { Container } from '@material-ui/core';
import SearchCardList from './SearchCardList.js';
import axios from 'axios';
import { useSelector } from 'react-redux';
import ReviewCardList from '../user/review/ReviewCardList.js';
import './Search.css';
import { Link } from 'react-router-dom';
import { useNavigate } from '../../../node_modules/react-router/index.js';

function Search() {
  const myuser = useSelector((state) => state.user.userData);
  const [itemList, setItemList] = useState([]);
  const [searchItem, setSearchItem] = useState('');
  const [searchReivewList, setSearchReivewList] = useState([]);
  const [searchUserList, setSearchUserList] = useState([]);
  const [searchLocationList, setSearchLocationList] = useState([]);
  const [searchFoodList, setSearchFoodList] = useState([]);
  const [searchHomeList, setSearchHomeList] = useState([]);
  const [searchPartyList, setSearchPartyList] = useState([]);
  const [noresult, setNoresult] = useState('');
  const [handsearch, setHandsearch] = useState(false);
  const navigate = useNavigate();

  const onSearchHandler = (event) => {
    setSearchItem(event.target.value);
  };

  const onSubmitHandler = (event) => {
    event.preventDefault();
    setSearchUserList([]);
    setSearchLocationList([]);
    setSearchFoodList([]);
    setSearchReivewList([]);
    setSearchHomeList([]);
    setSearchPartyList([]);
    if (searchItem) {
      setHandsearch(true);
      axios({
        method: 'GET',
        url: '/search/post',
        params: {
          keyword: searchItem,
          page: 0,
          size: 4,
          userSeq: myuser.userSeq,
        },
      }).then((res) => {
        setSearchReivewList(res.data);
      });

      axios({
        method: 'GET',
        url: '/search/user',
        params: {
          keyword: searchItem,
          page: 0,
          size: 4,
          userSeq: myuser.userSeq,
        },
      }).then((res) => {
        console.log(res.data);
        if (res.data.length > 0) {
          setSearchUserList(res.data);
        } else {
          setNoresult('ê²€ìƒ‰ ë‚´ìš©ì´ ì—†ìŠµë‹ˆë‹¤ ğŸ˜¥');
        }
      });
      axios({
        method: 'GET',
        url: '/search/tour',
        params: {
          contentTypeId: 12,
          keyword: searchItem,
          page: 0,
          size: 4,
          userSeq: myuser.userSeq,
        },
      }).then((res) => {
        // console.log(res.data)
        if (res.data.length > 0) {
          setSearchLocationList(res.data);
        } else {
          setNoresult('ê²€ìƒ‰ ë‚´ìš©ì´ ì—†ìŠµë‹ˆë‹¤ ğŸ˜¥');
        }
      });

      axios({
        method: 'GET',
        url: '/search/tour',
        params: {
          contentTypeId: 39,
          keyword: searchItem,
          page: 0,
          size: 4,
          userSeq: myuser.userSeq,
        },
      }).then((res) => {
        setSearchFoodList(res.data);
      });

      axios({
        method: 'GET',
        url: '/search/tour',
        params: {
          contentTypeId: 32,
          keyword: searchItem,
          page: 0,
          size: 4,
          userSeq: myuser.userSeq,
        },
      }).then((res) => {
        setSearchHomeList(res.data);
      });

      axios({
        method: 'GET',
        url: '/search/tour',
        params: {
          contentTypeId: 15,
          keyword: searchItem,
          page: 0,
          size: 4,
          userSeq: myuser.userSeq,
        },
      }).then((res) => {
        setSearchPartyList(res.data);
      });
    } else {
      alert('ê²€ìƒ‰ì–´ë¥¼ ì…ë ¥í•´ì£¼ì„¸ìš” ğŸ˜‰');
    }
  };

  const onClickLocation = () => {
    navigate(`/search/tour`, {
      state: {
        number: 12,
        searchItem: searchItem,
      },
    });
  };

  const onClickFood = () => {
    navigate(`/search/tour`, {
      state: {
        number: 39,
        searchItem: searchItem,
      },
    });
  };

  const onClickHome = () => {
    navigate(`/search/tour`, {
      state: {
        number: 32,
        searchItem: searchItem,
      },
    });
  };

  const onClickParty = () => {
    navigate(`/search/tour`, {
      state: {
        number: 15,
        searchItem: searchItem,
      },
    });
  };

  const onClickToSearch = () => {
    setHandsearch(false);
  };

  return (
    <div>
      <Container maxWidth="md">
        {handsearch == true ? (
          <div>
            <div>
              <p onClick={onClickToSearch}>ê²€ìƒ‰ì°½ìœ¼ë¡œ ëŒì•„ê°€ê¸°</p>
              {searchLocationList.length >= 1 ? (
                <div>
                  <h2>ëª…ì†Œ</h2>
                  <p onClick={onClickLocation}>ë”ë³´ê¸°</p>
                  <SearchCardList
                    itemList={searchLocationList}
                  ></SearchCardList>
                </div>
              ) : (
                <div>
                  <h2>ëª…ì†Œ</h2>
                  <p>{noresult}</p>
                </div>
              )}
            </div>
            <div>
              {searchFoodList.length >= 1 ? (
                <div>
                  <h2>ìŒì‹ì </h2>
                  <p onClick={onClickFood}>ë”ë³´ê¸°</p>
                  <SearchCardList itemList={searchFoodList}></SearchCardList>
                </div>
              ) : (
                <div>
                  <h2>ìŒì‹ì </h2>
                  <p>{noresult}</p>
                </div>
              )}
            </div>
            <div>
              {searchHomeList.length >= 1 ? (
                <div>
                  <h2>ìˆ™ë°•ì‹œì„¤</h2>
                  <p onClick={onClickHome}>ë”ë³´ê¸°</p>
                  <SearchCardList itemList={searchHomeList}></SearchCardList>
                </div>
              ) : (
                <div>
                  <h2>ìˆ™ë°•ì‹œì„¤</h2>
                  <p>{noresult}</p>
                </div>
              )}
            </div>
            <div>
              {searchPartyList.length >= 1 ? (
                <div>
                  <h2>í–‰ì‚¬</h2>
                  <p onClick={onClickParty}>ë”ë³´ê¸°</p>
                  <SearchCardList itemList={searchPartyList}></SearchCardList>
                </div>
              ) : (
                <div>
                  <h2>í–‰ì‚¬</h2>
                  <p>{noresult}</p>
                </div>
              )}
            </div>
            <div>
              {searchReivewList.length >= 2 ? (
                <div>
                  <h2>ì—¬í–‰ í›„ê¸°</h2>
                  {searchReivewList.map((review) => (
                    <p key={review.post_seq}>{review.post_title}</p>
                  ))}
                </div>
              ) : (
                <div>
                  <h2>ì—¬í–‰ í›„ê¸°</h2>
                  <p>{noresult}</p>
